  }
  Usuario.init({
    nom_usuario: DataTypes.STRING,
    contrasenia: DataTypes.STRING,
    correo: DataTypes.STRING,
    foto: DataTypes.STRING,
    tipoFoto: DataTypes.STRING,
    PersonaId: DataTypes.INTEGER
  }, {
    sequelize,
    modelName: 'Usuario',
  });

  return Usuario;
};

